  }, [
    responses.q2,
    responses.q6,
    responses.q6a,
    responses.q7,
    responses.q12,
    responses.q13,
    responses.q14,
    responses.q16,
    responses.q17,
    responses.q18,
    responses.q19,
    responses.q20,
    responses.q21,
    responses.q22,
    responses.q23,
    responses.q24,
    responses.q25,
    responses.q26,
    responses.q26b,
  ]);

  const handleRadioChange = (questionId) => (event) => {
    console.log(`Response updated`, { questionId, value: event.target.value });
    setResponses((prev) => ({
      ...prev,
      [questionId]: event.target.value,
    }));
  };

  const handleCheckboxChange = (questionId, optionValue) => (event) => {
    console.log(`Response updated`, {
      questionId,
      option: optionValue,
      checked: event.target.checked,
    });
    setResponses((prev) => ({
      ...prev,
      [questionId]: {
        ...prev[questionId],
        [optionValue]: event.target.checked,
      },
    }));
  };

  const handleNumberChange = (questionId) => (event) => {
    console.log(`Response updated`, { questionId, value: event.target.value });
    setResponses((prev) => ({
      ...prev,
      [questionId]: event.target.value,
    }));
  };

  const handleInputGroupChange = (questionId, optionValue) => (event) => {
    console.log(`Response updated`, { questionId, option: optionValue, value: event.target.value });
    setResponses((prev) => ({
      ...prev,
      [questionId]: {
        ...prev[questionId],
        [optionValue]: event.target.value,
      },
    }));
  };

  const handleInputGroupCheckboxChange = (questionId, optionValue) => (event) => {
    console.log(`Response updated`, {
      questionId,
      option: optionValue,
      checked: event.target.checked,
    });
    setResponses((prev) => ({
      ...prev,
      [questionId]: {
        ...prev[questionId],
        [optionValue]: event.target.checked,
      },
    }));
  };

  const renderQuestion = (question, depth = 0) => {
    if (question.showWhen && !question.showWhen(responses)) {
      return null;
    }

    return (
      <React.Fragment key={question.id}>
        <Paper
          sx={{
            p: 3,
            ...(depth > 0
              ? { ml: depth * 2, backgroundColor: 'grey.50', borderLeft: '4px solid', borderColor: 'primary.light' }
