            )}
            {question.type === 'checkbox' && (
              <Stack>
                {question.options.map((option) => (
                  <FormControlLabel
                    key={option.value}
                    control={
                      <Checkbox
                        checked={responses[question.id][option.value]}
                        onChange={handleCheckboxChange(question.id, option.value)}
                      />
                    }
                    label={option.label}
                  />
                ))}
              </Stack>
            )}
            {question.type === 'number' && (
              <TextField
                type="number"
                inputProps={{ min: 0 }}
                value={responses[question.id]}
                onChange={handleNumberChange(question.id)}
                label="Enter number"
              />
            )}
            {question.type === 'inputGroup' && (
              <Stack spacing={2}>
                {question.options.map((option) =>
                  option.control === 'checkbox' ? (
                    <FormControlLabel
                      key={option.value}
                      control={
                        <Checkbox
                          checked={Boolean(responses[question.id][option.value])}
                          onChange={handleInputGroupCheckboxChange(question.id, option.value)}
                        />
                      }
                      label={option.label}
                    />
                  ) : (
                    <TextField
                      key={option.value}
                      type="number"
                      inputProps={{ min: 0 }}
                      label={option.label}
                      value={responses[question.id][option.value]}
                      onChange={handleInputGroupChange(question.id, option.value)}
                    />
                  )
                )}
              </Stack>
            )}
          </Stack>
        </Paper>
        {question.children && question.children.map((child) => renderQuestion(child, depth + 1))}
      </React.Fragment>
    );
  };

  const totalMonthlyPrice = useMemo(() => calculateTotalMonthlyPrice(responses), [responses]);
  const serviceCatalogPricing = useSelector((state) => state.responses?.serviceCatalogPricing || 0);
  const combinedTotal = totalMonthlyPrice + serviceCatalogPricing;

  const totalOnceOffFee = useMemo(() => calculateTotalOnceOffFee(responses), [responses]);
  const serviceCatalogOnceOffFee = useSelector((state) => state.responses?.serviceCatalogOnceOffFee || 0);
  const combinedOnceOffTotal = totalOnceOffFee + serviceCatalogOnceOffFee;

  useEffect(() => {
    dispatch(setQuestionsPricing(totalMonthlyPrice));
  }, [totalMonthlyPrice, dispatch]);

  useEffect(() => {
    dispatch(setQuestionsOnceOffFee(totalOnceOffFee));
  }, [totalOnceOffFee, dispatch]);

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h3" sx={{ mb: 3 }}>
        Questions
      </Typography>
      <Paper sx={{ p: 3, mb: 4, backgroundColor: '#f5f5f5' }}>
        <Stack spacing={2}>
          <Typography variant="h5">Monthly Pricing</Typography>
          <Typography variant="h4" sx={{ color: '#1976d2', fontWeight: 'bold' }}>
            ${combinedTotal.toFixed(2)}
          </Typography>
        </Stack>
      </Paper>
      <Paper sx={{ p: 3, mb: 4, backgroundColor: '#f5f5f5' }}>
        <Stack spacing={2}>
          <Typography variant="h5">Once Off Fee</Typography>
          <Typography variant="h4" sx={{ color: '#1976d2', fontWeight: 'bold' }}>
            ${combinedOnceOffTotal.toFixed(2)}
          </Typography>
        </Stack>
      </Paper>
      <Stack spacing={3}>{questionData.filter((question) => question.id !== 'q1').map((question) => renderQuestion(question))}</Stack>
    </Container>
  );
}
