import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  q1: {},
  q2: '',
  q3: '',
  q4: '',
  q5: '',
  // Add more question keys as needed
  questionsPricing: 0,
  serviceCatalogPricing: 0,
  serviceSelections: {},
};

const responsesSlice = createSlice({
  name: 'responses',
  initialState,
  reducers: {
    setResponses: (state, action) => {
      Object.keys(state).forEach((key) => { 
        if (key !== 'questionsPricing' && key !== 'serviceCatalogPricing' && key !== 'serviceSelections') {
          delete state[key];
        }
      });
      Object.entries(action.payload).forEach(([key, value]) => {
        state[key] = value;
      });
    },
    updateResponse: (state, action) => {
      const { questionId, value } = action.payload;
      state[questionId] = value;
    },
    setQuestionsPricing: (state, action) => {
      state.questionsPricing = action.payload;
    },
    setServiceCatalogPricing: (state, action) => {
      state.serviceCatalogPricing = action.payload;
    },
    setServiceSelections: (state, action) => {
      state.serviceSelections = action.payload;
    },
  }
});

export const { setResponses, updateResponse, setQuestionsPricing, setServiceCatalogPricing, setServiceSelections } = responsesSlice.actions;
export default responsesSlice.reducer;
