import { serviceValuesAccounting } from '../constants/accountingServicesValues';

/**
 * Calculates total monthly pricing based on question responses
 * @param {Object} responses - Question responses from Questions.js
 * @returns {number} Total monthly cost
 */
export const calculateTotalMonthlyPrice = (responses) => {
  let total = 0;

  // Helper function to get segment for service lookup
  const getSegment = (originalSegment) => {
    if (['micro', 'small', 'medium', 'large'].includes(originalSegment)) {
      return originalSegment;
    }
    if (originalSegment === 'enterprise') {
      return 'large';
    }
    return null;
  };

  const segment = getSegment(responses.q2);

  // q1: Services provided (checkbox) - not directly a service cost
  // q2: Revenue segment - used for lookups
  // q3: Accounting system - not directly a service cost
  // q3a: Set up system - not directly a service cost
  // q3b: Information method - not directly a service cost
  // q4: Number of business entities
  if (responses.q4 && responses.q4 !== '' && segment) {
    const businessCount = parseInt(responses.q4, 10);
    if (!isNaN(businessCount) && businessCount > 0) {
      // Business returns pricing per entity
      const businessReturn = serviceValuesAccounting.taxServices.businessReturns[segment];
      if (businessReturn) {
        total += businessReturn.monthly * businessCount;
      }
    }
  }

  // q5: Number of individual returns
  if (responses.q5 && responses.q5 !== '') {
    const individualCount = parseInt(responses.q5, 10);
    if (!isNaN(individualCount) && individualCount > 0) {
      const individualReturn = serviceValuesAccounting.taxServices.individualReturns.all;
      if (individualReturn) {
        total += individualReturn.monthly * individualCount;
      }
    }
  }

  // q6: SMSF
  if (responses.q6 === 'yes' && segment) {
    const smsfService = serviceValuesAccounting.taxServices.smsf[segment];
    if (smsfService) {
      total += smsfService.monthly;
    }
  }

  // q6a: SMSF audit and tax return
  if (responses.q6a === 'yes' && segment) {
    const smsfService = serviceValuesAccounting.taxServices.smsf[segment];
    if (smsfService) {
      total += smsfService.monthly;
    }
  }

  // q7: BAS/IAS
  if (responses.q7 && responses.q7 !== '' && segment) {
    if (responses.q7 === 'basQuarterly' || responses.q7 === 'basMonthly') {
      const basService = serviceValuesAccounting.taxServices.bas[segment];
      if (basService) {
        total += basService.monthly;
      }
    } else if (responses.q7 === 'iasMonthly') {
      const iasService = serviceValuesAccounting.taxServices.ias[segment];
      if (iasService) {
        total += iasService.monthly;
      }
    }
  }

  // q8: Run payroll - not directly a service cost
  // q8a: Payroll processing - not directly mapped (varies by employee count)
  // q9: Salaried employees
  if (responses.q9 && typeof responses.q9 === 'object' && segment) {
    const { weekly = 0, fortnightly = 0, monthly = 0 } = responses.q9;
    const weeklyCount = parseInt(weekly, 10) || 0;
    const fortnightlyCount = parseInt(fortnightly, 10) || 0;
    const monthlyCount = parseInt(monthly, 10) || 0;

    const totalSalaried = weeklyCount + fortnightlyCount + monthlyCount;
    if (totalSalaried > 0) {
      const payrollProcessing = serviceValuesAccounting.payrollServices.payrollProcessing?.salary;
      if (payrollProcessing) {
        total += payrollProcessing.monthly * totalSalaried;
      }
    }
  }

  // q10: Timesheet employees
  if (responses.q10 && typeof responses.q10 === 'object' && segment) {
    const { weekly = 0, fortnightly = 0, monthly = 0 } = responses.q10;
    const weeklyCount = parseInt(weekly, 10) || 0;
    const fortnightlyCount = parseInt(fortnightly, 10) || 0;
    const monthlyCount = parseInt(monthly, 10) || 0;

    const totalTimesheet = weeklyCount + fortnightlyCount + monthlyCount;
    if (totalTimesheet > 0) {
      const payrollProcessing = serviceValuesAccounting.payrollServices.payrollProcessing?.timesheets;
      if (payrollProcessing) {
        total += payrollProcessing.monthly * totalTimesheet;
      }
    }
  }

  // q11: Single Touch Payroll
  if (responses.q11 && responses.q11 !== 'no' && segment) {
    const stpService = serviceValuesAccounting.payrollServices.stpReporting?.[segment];
    if (stpService) {
      total += stpService.monthly;
    }
  }

  // q12: Superannuation lodgement
  if (responses.q12 && responses.q12 !== 'no' && segment) {
    const superService = serviceValuesAccounting.payrollServices.superPrepAndLodgement?.[segment];
    if (superService) {
      total += superService.monthly;
    }
  }

  // q13: Payroll tax returns
  if (responses.q13 === 'yes' && segment) {
    const payrollTaxService = serviceValuesAccounting.payrollServices.payrollTaxReturns?.[segment];
    if (payrollTaxService) {
      total += payrollTaxService.monthly;
    }
  }

  // q14: Workers compensation
  if (responses.q14 === 'yes' && segment) {
    const workersCompService = serviceValuesAccounting.payrollServices.workersCompensation?.[segment];
    if (workersCompService) {
      total += workersCompService.monthly;
    }
  }

  // q15: Long service leave - not directly mapped in accountingServicesValues
  // q16: TPAR
  if (responses.q16 === 'yes' && segment) {
    const tparService = serviceValuesAccounting.taxServices.tpar?.[segment];
    if (tparService) {
      total += tparService.monthly;
    }
  }

  // q17: FBT return
  if (responses.q17 === 'yes' && segment) {
    const fbtService = serviceValuesAccounting.taxServices.fbtReturns?.[segment];
    if (fbtService) {
      total += fbtService.monthly;
    }
  }

  // q18: Tax planning
  if (responses.q18 === 'yes' && segment) {
    const taxPlanningService = serviceValuesAccounting.advisoryServices.taxPlanningReview?.[segment];
    if (taxPlanningService) {
      total += taxPlanningService.monthly;
    }
  }

  // q19: Tax structuring
  if (responses.q19 === 'yes' && segment) {
    const taxStructuringService = serviceValuesAccounting.advisoryServices.taxStructuringAdvice?.[segment];
    if (taxStructuringService) {
      total += taxStructuringService.monthly;
    }
  }

  // q20: Financial statements
  if (responses.q20 === 'yes' && segment) {
    const fsService = serviceValuesAccounting.reporting.financialStatementsTax?.[segment];
    if (fsService) {
      total += fsService.monthly;
    }
  }

  // q21: Statutory financial statements
  if (responses.q21 === 'yes' && segment) {
    const statutoryService = serviceValuesAccounting.reporting.statutoryFinancialStatements?.[segment];
    if (statutoryService) {
      total += statutoryService.monthly;
    }
  }

  // q22: Management financial statements
  if (responses.q22 && responses.q22 !== 'no' && segment) {
    const mfsService = serviceValuesAccounting.reporting.managementFinancialStatements?.[segment];
    if (mfsService) {
      total += mfsService.monthly;
    }
  }

  // q23: Review the Numbers meetings
  if (responses.q23 && responses.q23 !== 'no' && segment) {
    const reviewService = serviceValuesAccounting.meetings.reviewNumbers?.[segment];
    if (reviewService) {
      total += reviewService.monthly;
    }
  }

  // q24: Annual tax meetings
  if (responses.q24 === 'yes' && segment) {
    const annualService = serviceValuesAccounting.meetings.annualTaxMeetings?.[segment];
    if (annualService) {
      total += annualService.monthly;
    }
  }

  // q25: Support level
  if (responses.q25 && responses.q25 !== '' && segment) {
    if (responses.q25 === 'emailTeam' || responses.q25 === 'emailPhoneTeamCsm') {
      const teamSupport = serviceValuesAccounting.supportServices.teamOrEmail?.[segment];
      if (teamSupport) {
        total += teamSupport.monthly;
      }
    }
    if (responses.q25 === 'emailPhoneTeamCsm' || responses.q25 === 'emailPhoneCsmOwner') {
      const csmSupport = serviceValuesAccounting.supportServices.clientServiceManager?.[segment];
      if (csmSupport) {
        total += csmSupport.monthly;
      }
    }
    if (responses.q25 === 'emailPhoneCsmOwner') {
      const ownerSupport = serviceValuesAccounting.supportServices.principalOwner?.[segment];
      if (ownerSupport) {
        total += ownerSupport.monthly;
      }
    }
  }

  // q26: ASIC company secretarial work
  if (responses.q26 && responses.q26 !== '' && responses.q26 !== 'no') {
    if (responses.q26 === 'annualReturns') {
      const asicService = serviceValuesAccounting.corporateSecretarial.asicAnnualReturn;
      if (asicService) {
        total += asicService.monthly;
      }
    } else if (responses.q26 === 'detailChanges') {
      const asicService = serviceValuesAccounting.corporateSecretarial.asicFormsLodgements;
      if (asicService) {
        total += asicService.monthly;
      }
    }
  }

  // q26b: ATO payment plans
  if (responses.q26b && responses.q26b !== '' && responses.q26b !== 'no') {
    if (responses.q26b === 'basicPlans') {
      const atoPlan = serviceValuesAccounting.atoPaymentPlans.basicPlans;
      if (atoPlan) {
        total += atoPlan.monthly || 0;
      }
    } else if (responses.q26b === 'hardshipPlans') {
      const atoPlan = serviceValuesAccounting.atoPaymentPlans.hardshipPlans;
      if (atoPlan) {
        total += atoPlan.monthly || 0;
      }
    }
  }

  // q27: Prior year lodgements (inputGroup - multiply counts by service monthly value)
  if (responses.q27 && typeof responses.q27 === 'object') {
    const priorYearServices = responses.q27;

    // Business returns
    if (priorYearServices.business && priorYearServices.business !== '') {
      const count = parseInt(priorYearServices.business, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.businessReturns?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // Individual returns
    if (priorYearServices.individuals && priorYearServices.individuals !== '') {
      const count = parseInt(priorYearServices.individuals, 10) || 0;
      if (count > 0) {
        const service = serviceValuesAccounting.taxServices.individualReturns?.all;
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // BAS returns
    if (priorYearServices.bas && priorYearServices.bas !== '') {
      const count = parseInt(priorYearServices.bas, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.bas?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // SMSF
    if (priorYearServices.smsf && priorYearServices.smsf !== '') {
      const count = parseInt(priorYearServices.smsf, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.smsf?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // IAS
    if (priorYearServices.ias && priorYearServices.ias !== '') {
      const count = parseInt(priorYearServices.ias, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.ias?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // FBT
    if (priorYearServices.fbt && priorYearServices.fbt !== '') {
      const count = parseInt(priorYearServices.fbt, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.fbtReturns?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // TPAR
    if (priorYearServices.tpar && priorYearServices.tpar !== '') {
      const count = parseInt(priorYearServices.tpar, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.taxServices.tpar?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // Workers comp
    if (priorYearServices.workersComp && priorYearServices.workersComp !== '') {
      const count = parseInt(priorYearServices.workersComp, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.payrollServices.workersCompensation?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // Super lodgement
    if (priorYearServices.super && priorYearServices.super !== '') {
      const count = parseInt(priorYearServices.super, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.payrollServices.superPrepAndLodgement?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // STP EOY
    if (priorYearServices.stpEoy && priorYearServices.stpEoy !== '') {
      const count = parseInt(priorYearServices.stpEoy, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.payrollServices.stpReporting?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // LSL forms
    if (priorYearServices.lslForms && priorYearServices.lslForms !== '') {
      const count = parseInt(priorYearServices.lslForms, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.payrollServices.lslReporting?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // Payroll tax
    if (priorYearServices.payrollTax && priorYearServices.payrollTax !== '') {
      const count = parseInt(priorYearServices.payrollTax, 10) || 0;
      if (count > 0 && segment) {
        const service = serviceValuesAccounting.payrollServices.payrollTaxReturns?.[segment];
        if (service) {
          total += service.monthly * count;
        }
      }
    }

    // ASIC
    if (priorYearServices.asic && priorYearServices.asic !== '') {
      const count = parseInt(priorYearServices.asic, 10) || 0;
      if (count > 0) {
        const service = serviceValuesAccounting.corporateSecretarial.asicAnnualReturn;
        if (service) {
          total += service.monthly * count;
        }
      }
    }
  }

  return Math.round(total * 100) / 100; // Round to 2 decimal places
};

/**
 * Calculates total once-off (yearly) fees based on question responses
 * @param {Object} responses - Question responses from Questions.js
 * @returns {number} Total once-off fee
 */
export const calculateTotalOnceOffFee = (responses) => {
  let total = 0;

  // Helper function to get segment for service lookup
  const getSegment = (originalSegment) => {
    if (['micro', 'small', 'medium', 'large'].includes(originalSegment)) {
      return originalSegment;
    }
    if (originalSegment === 'enterprise') {
      return 'large';
    }
    return null;
  };

  const segment = getSegment(responses.q2);

  // q3a: System setup
  if (responses.q3a === 'yes' && segment && serviceValuesAccounting?.taxServices?.xeroSetup) {
    const xeroSetup = serviceValuesAccounting.taxServices.xeroSetup[segment];
    if (xeroSetup) {
      total += xeroSetup.yearly;
    }
  }

  // q4: Number of business entities
  if (responses.q4 && responses.q4 !== '' && segment) {
    const businessCount = parseInt(responses.q4, 10);
    if (!isNaN(businessCount) && businessCount > 0) {
      const businessReturn = serviceValuesAccounting.taxServices.businessReturns[segment];
      if (businessReturn) {
        total += businessReturn.yearly * businessCount;
      }
    }
  }

  // q5: Number of individual returns
  if (responses.q5 && responses.q5 !== '') {
    const individualCount = parseInt(responses.q5, 10);
    if (!isNaN(individualCount) && individualCount > 0) {
      const individualReturn = serviceValuesAccounting.taxServices.individualReturns.all;
      if (individualReturn) {
        total += individualReturn.yearly * individualCount;
      }
    }
  }

  // q6: SMSF setup
  if (responses.q6 === 'yes' && segment) {
    const smsfSetup = serviceValuesAccounting.taxServices.smsf[segment];
    if (smsfSetup) {
      total += smsfSetup.yearly;
    }

    // q6a: FBT returns
    if (responses.q6a === 'yes' && segment) {
      const fbtReturns = serviceValuesAccounting.taxServices.fbtReturns[segment];
      if (fbtReturns) {
        total += fbtReturns.yearly;
      }
    }
  }

  // q7: BAS or IAS
  if (responses.q7 && responses.q7 !== 'no' && segment) {
    if (responses.q7 === 'BAS') {
      const bas = serviceValuesAccounting.taxServices.bas[segment];
      if (bas) {
        total += bas.yearly;
      }
    } else if (responses.q7 === 'IAS') {
      const ias = serviceValuesAccounting.taxServices.ias[segment];
      if (ias) {
        total += ias.yearly;
      }
    }
  }

  // q8: Payroll setup
  if (responses.q8 === 'yes' && segment) {
    const payroll = serviceValuesAccounting.payrollServices.payroll[segment];
    if (payroll) {
      total += payroll.yearly;
    }
  }

  // q9: Number of employees
  if (responses.q9 && responses.q9 !== '' && segment) {
    const employeeCount = parseInt(responses.q9, 10);
    if (!isNaN(employeeCount) && employeeCount > 0) {
      const payrollProcessing = serviceValuesAccounting.payrollServices.payrollProcessing[segment];
      if (payrollProcessing) {
        total += payrollProcessing.yearly * employeeCount;
      }
    }
  }

  // q10: Number of contractors
  if (responses.q10 && responses.q10 !== '') {
    const contractorCount = parseInt(responses.q10, 10);
    if (!isNaN(contractorCount) && contractorCount > 0) {
      const groupCert = serviceValuesAccounting.payrollServices.groupCertificates.all;
      if (groupCert) {
        total += groupCert.yearly * contractorCount;
      }
    }
  }

  // q11: Super adjustment reporting
  if (responses.q11 === 'yes' && segment) {
    const superReporting = serviceValuesAccounting.payrollServices.superAdjustmentReporting[segment];
    if (superReporting) {
      total += superReporting.yearly;
    }
  }

  // q12: TPAR preparation
  if (responses.q12 === 'yes' && segment) {
    const tpar = serviceValuesAccounting.payrollServices.tpar[segment];
    if (tpar) {
      total += tpar.yearly;
    }
  }

  // q13: Fringe benefit reporting
  if (responses.q13 === 'yes' && segment) {
    const fbtReporting = serviceValuesAccounting.payrollServices.fbtReporting[segment];
    if (fbtReporting) {
      total += fbtReporting.yearly;
    }
  }

  // q14: WorkCover premium management
  if (responses.q14 === 'yes' && segment) {
    const workCover = serviceValuesAccounting.payrollServices.workCoverManagement[segment];
    if (workCover) {
      total += workCover.yearly;
    }
  }

  // q15: Super processing
  if (responses.q15 === 'yes' && segment) {
    const superProcessing = serviceValuesAccounting.payrollServices.superProcessing[segment];
    if (superProcessing) {
      total += superProcessing.yearly;
    }
  }

  // q16: DGR status
  if (responses.q16 === 'yes' && segment) {
    const dgr = serviceValuesAccounting.taxServices.dgrReturns[segment];
    if (dgr) {
      total += dgr.yearly;
    }
  }

  // q17: TPAR for tax practice
  if (responses.q17 === 'yes' && segment) {
    const tparTax = serviceValuesAccounting.taxServices.tpar[segment];
    if (tparTax) {
      total += tparTax.yearly;
    }
  }

  // q18: Tax planning
  if (responses.q18 === 'yes' && segment) {
    const taxPlanning = serviceValuesAccounting.advisoryServices.taxPlanning[segment];
    if (taxPlanning) {
      total += taxPlanning.yearly;
    }
  }

  // q19: Tax structuring
  if (responses.q19 === 'yes' && segment) {
    const taxStructuring = serviceValuesAccounting.advisoryServices.taxStructuring[segment];
    if (taxStructuring) {
      total += taxStructuring.yearly;
    }
  }

  // q20: Financial statements
  if (responses.q20 === 'yes' && segment) {
    const financialStatements = serviceValuesAccounting.reporting.financialStatements[segment];
    if (financialStatements) {
      total += financialStatements.yearly;
    }
  }

  // q21: Management accounts
  if (responses.q21 === 'yes' && segment) {
    const managementAccounts = serviceValuesAccounting.reporting.managementAccounts[segment];
    if (managementAccounts) {
      total += managementAccounts.yearly;
    }
  }

  // q22: KMP reporting
  if (responses.q22 === 'yes' && segment) {
    const kmpReporting = serviceValuesAccounting.reporting.kmpReporting[segment];
    if (kmpReporting) {
      total += kmpReporting.yearly;
    }
  }

  // q23: Quarterly business review
  if (responses.q23 === 'yes' && segment) {
    const qbr = serviceValuesAccounting.meetings.qbr[segment];
    if (qbr) {
      total += qbr.yearly;
    }
  }

  // q24: Annual strategy session
  if (responses.q24 === 'yes' && segment) {
    const strategy = serviceValuesAccounting.meetings.annualStrategy[segment];
    if (strategy) {
      total += strategy.yearly;
    }
  }

  // q25: Support services
  if (responses.q25 && responses.q25 !== 'no') {
    const supportTier = serviceValuesAccounting.supportServices[responses.q25];
    if (supportTier) {
      total += supportTier.yearly;
    }

    // Add team member costs if applicable
    if (responses.q25a && responses.q25a !== '') {
      const teamCount = parseInt(responses.q25a, 10);
      if (!isNaN(teamCount) && teamCount > 0) {
        total += (supportTier.teamMemberCost?.yearly || 0) * teamCount;
      }
    }
  }

  // q26: Corporate secretarial
  if (responses.q26 === 'yes' && segment) {
    const corpSec = serviceValuesAccounting.corporateSecretarial.corporateSecretarial[segment];
    if (corpSec) {
      total += corpSec.yearly;
    }
  }

  // q27: Prior year lodgements
  if (responses.q27 && responses.q27 !== '') {
    const priorYearCount = parseInt(responses.q27, 10);
    if (!isNaN(priorYearCount) && priorYearCount > 0) {
      const priorYear = serviceValuesAccounting.taxServices.priorYearLodgements.all;
      if (priorYear) {
        total += priorYear.yearly * priorYearCount;
      }
    }
  }

  return Math.round(total * 100) / 100; // Round to 2 decimal places
};
