import React, { useEffect } from 'react';
import { ThemeProvider, CssBaseline, AppBar, Toolbar, Button, Typography } from '@mui/material';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { loginSuccess, logout } from './features/auth/authSlice';
import { useAuth } from '@workos-inc/authkit-react';
import theme from './theme';
import Home from './pages/Home';
import About from './pages/About';
import Questions from './pages/Questions';
import ServiceCatalog from './pages/ServiceCatalog';
import Login from './features/auth/Login';
import ProtectedRoute from './features/auth/ProtectedRoute';

function App() {
  const storedUser = useSelector((state) => state.auth.user);
  const dispatch = useDispatch();
  const { user, isLoading, signOut } = useAuth();

  useEffect(() => {
    if (isLoading) return;

    if (user && storedUser?.id !== user.id) {
      dispatch(loginSuccess(user));
    }

    if (!user && storedUser) {
      dispatch(logout());
    }
  }, [dispatch, isLoading, storedUser, user]);

  const activeUser = user ?? storedUser;

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <BrowserRouter>
        <AppBar position="static">
          <Toolbar>
            <Button color="inherit" component={Link} to="/">Home</Button>
            <Button color="inherit" component={Link} to="/about">About</Button>
            <Button color="inherit" component={Link} to="/questions">Questions</Button>
            <Button color="inherit" component={Link} to="/services">Services</Button>
            {!activeUser && <Button color="inherit" component={Link} to="/login">Login</Button>}
            {activeUser && (
              <>
                <Typography sx={{ flexGrow: 1, ml: 2 }}>{activeUser.email}</Typography>
                <Button color="inherit" onClick={() => signOut({ returnTo: window.location.origin })}>
                  Logout
                </Button>
              </>
            )}
          </Toolbar>
        </AppBar>
        <Routes>
          <Route path="/" element={<ProtectedRoute><Home /></ProtectedRoute>} />
          <Route path="/about" element={<About />} />
          <Route path="/questions" element={<Questions />} />
          <Route path="/services" element={<ServiceCatalog />} />
          <Route path="/login" element={<Login />} />
        </Routes>
      </BrowserRouter>
    </ThemeProvider>
  );
}

export default App;
